import '../css/banner.css';
import React from 'react';


class Banner extends React.Component {

    state = {
        color: 'rgba(0, 0, 0, 0.7)',
        lastScrollY: 0,  // Keep track of current position in state
    };

    componentDidMount() {
        window.onscroll = () => {
            const newScrollHeight = Math.ceil(window.scrollY / 50) * 50;
            if (this.state.currentScrollHeight != newScrollHeight) {
                this.setState({ currentScrollHeight: newScrollHeight })
            }
        }
    }

    handleScroll = () => {
        const { lastScrollY } = this.state;
        const currentScrollY = window.scrollY;


        if (currentScrollY > lastScrollY) {
            this.setState({ color: 'rgba(0, 0, 0, 0)' });
        } else {
            this.setState({ color: 'rgba(0, 0, 0, 0.7)' });
        }
        this.setState({ lastScrollY: currentScrollY });
    };


    render() {
        return (
            <div className="bannerOverlay" style={{
                transition: 'background 0.2s ease',
                background: `${this.state.slide}`,
            }
            }>
                <h1 className="bannerNameHello">Hello! I'm </h1>
                <h1 className="bannerName">Yuxuan Yao</h1>
            </div >
        );
    }
}

export default Banner;
